from datetime import datetime
import json

def is_valid_date(day, month, year):
    try:
        # Создаем объект даты
        dt = datetime(year, month, day)

        # Проверяем, что день, месяц и год совпадают
        if dt.day != day or dt.month != month or dt.year != year:
            return False
        return True
    except ValueError:
        # Если дата невалидна, вернем False
        return False

def calculate(x):
    # Убедитесь, что x - это строка длиной не менее 12 символов
    if len(x) < 12:
        raise ValueError("Строка должна содержать как минимум 12 символов.")

    # Суммируем первые 12 цифр
    chislo = sum(int(x[i]) for i in range(12))

    # Получаем цифровую сумму
    chislo = sumnum(chislo)
    with open('app/data.json', 'r', encoding='utf-8') as file:
        data = json.load(file)

    for item in data:
        if item['id'] == chislo:
            text = item['text']
            photo = item['photo_ids']
    return chislo , str(text),str(photo)
