def sumnum(x):
    if len(str(x)) == 2:
        x1 = int(str(x)[0]) + int(str(x)[1])
        return sumnum(x1)
    return x




def validate(den, mes, denR, mesR, godR):
    if not is_valid_date(int(den), int(mes), 2012):
        print("Неверно введена дата")
        return

    if not is_valid_date(int(denR), int(mesR), int(godR)):
        print("Неверно введена дата")
        return
    if len(str(den)) !=2:
        den = "0"+ str(den)
    if len(str(mes)) !=2:
        mes = "0"+ str(mes)
    if len(str(denR)) !=2:
        denR = "0"+ str(denR)
    if len(str(mesR)) !=2:
        mesR = "0"+ str(mesR)
    dr = f"{den}{mes}{denR}{mesR}{godR}"
    return calculate(dr)







